def minCostGoodCaption(caption):
    n = len(caption)
    if n < 3:
        return ""

    def cost(c1, c2):
        return abs(ord(c1) - ord(c2))

    min_cost = float('inf')
    best_result = ""

    for target_char in 'abcdefghijklmnopqrstuvwxyz':
        current_cost = 0
        for char in caption:
            current_cost += cost(char, target_char)

        if current_cost < min_cost or (current_cost == min_cost and target_char < best_result[0] if best_result else True):
            min_cost = current_cost
            best_result = target_char * n

    return best_result
