def largestInteger(nums, k):
    count = {}

    for i in range(len(nums) - k + 1):
        subarray = nums[i:i+k]
        for num in set(subarray):
            count[num] = count.get(num, 0) + 1

    result = -1
    for num, freq in count.items():
        if freq == 1:
            result = max(result, num)

    return result if result != -1 else -1
