from collections import Counter

def findValidPair(s):
    count = Counter(s)

    for i in range(len(s) - 1):
        first_digit = s[i]
        second_digit = s[i + 1]

        if first_digit != second_digit:
            if count[first_digit] == int(first_digit) and count[second_digit] == int(second_digit):
                return first_digit + second_digit

    return ""
