def separateSquares(squares):
    events = []

    for x, y, l in squares:
        events.append((y, l * l))
        events.append((y + l, -l * l))

    events.sort()

    total_area = sum(l * l for _, _, l in squares)
    target = total_area / 2

    current_area = 0
    prev_y = None

    for y, area_change in events:
        if prev_y is not None and current_area < target:
            remaining = target - current_area
            active_width = 0

            for x, sy, l in squares:
                if sy <= prev_y < sy + l:
                    active_width += l

            if active_width > 0:
                delta_y = remaining / active_width
                if prev_y + delta_y <= y:
                    return prev_y + delta_y

        current_area += area_change
        prev_y = y

        if current_area >= target:
            return y

    return prev_y
