def maxProduct(nums, k, limit):
    n = len(nums)
    memo = {}

    def dp(i, current_sum, current_product, length):
        if i == n:
            if current_sum == k and length > 0 and current_product <= limit:
                return current_product
            return -1

        state = (i, current_sum, current_product, length)
        if state in memo:
            return memo[state]

        result = dp(i + 1, current_sum, current_product, length)

        if current_product == 1:
            new_product = nums[i]
        else:
            new_product = current_product * nums[i]

        if new_product <= limit:
            if length % 2 == 0:
                new_sum = current_sum + nums[i]
            else:
                new_sum = current_sum - nums[i]

            include_result = dp(i + 1, new_sum, new_product, length + 1)
            if include_result != -1:
                result = max(result, include_result)

        memo[state] = result
        return result

    return dp(0, 0, 1, 0)
