def candidate_func(input_str):
    lines = input_str.strip().split('\n')
    n = int(lines[0])
    stones = list(map(int, lines[1].split()))

    for year in range(1, n + 1):
        adults_with_stones = 0
        for i in range(year):
            if stones[i] > 0:
                adults_with_stones += 1

        for i in range(year):
            if stones[i] > 0:
                stones[i] -= 1

        stones[year - 1] += adults_with_stones

    return ' '.join(map(str, stones))
