import math
from collections import defaultdict

def candidate_func(input_str):
    lines = input_str.strip().split('\n')
    n, k = map(int, lines[0].split())
    a = list(map(int, lines[1].split()))

    max_val = max(a)

    divisors = [[] for _ in range(max_val + 1)]
    for i in range(1, max_val + 1):
        for j in range(i, max_val + 1, i):
            divisors[j].append(i)

    gcd_count = [0] * (max_val + 1)
    for val in a:
        for d in divisors[val]:
            gcd_count[d] += 1

    results = []

    for i in range(n):
        ai = a[i]
        max_gcd = 1

        for d in divisors[ai]:
            if gcd_count[d] >= k:
                max_gcd = max(max_gcd, d)

        results.append(str(max_gcd))

    return '\n'.join(results)
