import heapq
from collections import defaultdict

def candidate_func(input_str):
    lines = input_str.strip().split('\n')
    H, W = map(int, lines[0].split())

    F = []
    for i in range(1, H + 1):
        F.append(list(map(int, lines[i].split())))

    Q = int(lines[H + 1])

    results = []

    for q in range(Q):
        A, B, Y, C, D, Z = map(int, lines[H + 2 + q].split())
        A -= 1
        B -= 1
        C -= 1
        D -= 1

        pq = [(0, A, B, Y)]
        visited = set()

        while pq:
            stairs, r, c, floor = heapq.heappop(pq)

            if (r, c, floor) in visited:
                continue
            visited.add((r, c, floor))

            if r == C and c == D and floor == Z:
                results.append(str(stairs))
                break

            for new_floor in range(1, floor):
                if (r, c, new_floor) not in visited:
                    heapq.heappush(pq, (stairs + floor - new_floor, r, c, new_floor))

            for new_floor in range(floor + 1, F[r][c] + 1):
                if (r, c, new_floor) not in visited:
                    heapq.heappush(pq, (stairs + new_floor - floor, r, c, new_floor))

            for dr, dc in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
                nr, nc = r + dr, c + dc
                if 0 <= nr < H and 0 <= nc < W and F[nr][nc] >= floor:
                    if (nr, nc, floor) not in visited:
                        heapq.heappush(pq, (stairs, nr, nc, floor))

    return '\n'.join(results)
