def candidate_func(input_str):
    lines = input_str.strip().split('\n')
    n, m = map(int, lines[0].split())

    parent = list(range(n + 1))
    color = [0] * (n + 1)

    def find(x):
        if parent[x] != x:
            old_parent = parent[x]
            parent[x] = find(parent[x])
            color[x] ^= color[old_parent]
        return parent[x]

    def union(x, y):
        root_x = find(x)
        root_y = find(y)
        if root_x != root_y:
            parent[root_y] = root_x
            color[root_y] = color[x] ^ color[y] ^ 1

    for i in range(1, m + 1):
        u, v = map(int, lines[i].split())
        union(u, v)

    components = {}
    for i in range(1, n + 1):
        root = find(i)
        if root not in components:
            components[root] = []
        components[root].append(i)

    total_moves = 0
    for comp in components.values():
        size = len(comp)
        color_0 = sum(1 for v in comp if color[v] == 0)
        color_1 = size - color_0
        total_moves += color_0 * color_1

    total_moves -= m

    if total_moves % 2 == 1:
        return "Aoki"
    else:
        return "Takahashi"
