from collections import deque

def candidate_func(input_str):
    lines = input_str.strip().split('\n')
    H, W = map(int, lines[0].split())
    grid = []
    for i in range(1, H + 1):
        grid.append(list(lines[i]))
    A, B, C, D = map(int, lines[H + 1].split())
    A -= 1
    B -= 1
    C -= 1
    D -= 1

    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]

    def can_reach(grid_state, start_r, start_c, end_r, end_c):
        if grid_state[start_r][start_c] == '#' or grid_state[end_r][end_c] == '#':
            return False
        visited = [[False] * W for _ in range(H)]
        queue = deque([(start_r, start_c)])
        visited[start_r][start_c] = True

        while queue:
            r, c = queue.popleft()
            if r == end_r and c == end_c:
                return True
            for dr, dc in directions:
                nr, nc = r + dr, c + dc
                if 0 <= nr < H and 0 <= nc < W and not visited[nr][nc] and grid_state[nr][nc] == '.':
                    visited[nr][nc] = True
                    queue.append((nr, nc))
        return False

    if can_reach(grid, A, B, C, D):
        return "0"

    queue = deque([(grid, A, B, 0)])
    visited = set()

    def grid_to_tuple(g):
        return tuple(tuple(row) for row in g)

    visited.add((grid_to_tuple(grid), A, B))

    while queue:
        current_grid, r, c, kicks = queue.popleft()

        for dr, dc in directions:
            nr, nc = r + dr, c + dc
            if 0 <= nr < H and 0 <= nc < W and current_grid[nr][nc] == '.':
                if nr == C and nc == D:
                    return str(kicks)
                state = (grid_to_tuple(current_grid), nr, nc)
                if state not in visited:
                    visited.add(state)
                    queue.append((current_grid, nr, nc, kicks))

        for dr, dc in directions:
            new_grid = [row[:] for row in current_grid]
            for step in range(1, 3):
                kick_r, kick_c = r + dr * step, c + dc * step
                if 0 <= kick_r < H and 0 <= kick_c < W and new_grid[kick_r][kick_c] == '#':
                    new_grid[kick_r][kick_c] = '.'

            if can_reach(new_grid, r, c, C, D):
                return str(kicks + 1)

            state = (grid_to_tuple(new_grid), r, c)
            if state not in visited:
                visited.add(state)
                queue.append((new_grid, r, c, kicks + 1))

    return "-1"
