def candidate_func(input_str):
    lines = input_str.strip().split('\n')
    n, m = map(int, lines[0].split())

    operations = []
    for i in range(1, m + 1):
        l, r = map(int, lines[i].split())
        operations.append((l, r))

    min_cost = float('inf')
    best_solution = None

    for mask in range(3 ** m):
        x = [0] * (n + 1)
        cost = 0
        ops = []
        temp_mask = mask

        for i in range(m):
            op = temp_mask % 3
            temp_mask //= 3
            ops.append(op)

            l, r = operations[i]

            if op == 1:
                cost += 1
                for j in range(l, r + 1):
                    x[j] = 1
            elif op == 2:
                cost += 1
                for j in range(1, n + 1):
                    if j < l or j > r:
                        x[j] = 1

        if all(x[j] == 1 for j in range(1, n + 1)):
            if cost < min_cost:
                min_cost = cost
                best_solution = ops

    if best_solution is None:
        return "-1"
    else:
        result = str(min_cost) + "\n" + " ".join(map(str, best_solution))
        return result
