import sys

def minCostGoodCaption(caption):
    n = len(caption)
    if n < 3:
        return ""
    min_operations = sys.maxsize
    result = ""
    for c in set(caption):
        operations = 0
        possible = True
        temp = []
        for i in range(n):
            current = caption[i]
            if current == c:
                temp.append(c)
                continue
            diff = ord(current) - ord(c)
            if diff == 1 or diff == -1:
                operations += 1
                temp.append(c)
            else:
                possible = False
                break
        if possible:
            if operations < min_operations:
                min_operations = operations
                result = ''.join(temp)
            elif operations == min_operations:
                candidate = ''.join(temp)
                if candidate < result:
                    result = candidate
    for c in set(caption):
        for delta in [-1, 1]:
            target = chr(ord(c) + delta)
            if target < 'a' or target > 'z':
                continue
            operations = 0
            possible = True
            temp = []
            for i in range(n):
                current = caption[i]
                if current == target:
                    temp.append(target)
                    continue
                diff = ord(current) - ord(target)
                if diff == 1 or diff == -1:
                    operations += 1
                    temp.append(target)
                else:
                    possible = False
                    break
            if possible:
                if operations < min_operations:
                    min_operations = operations
                    result = ''.join(temp)
                elif operations == min_operations:
                    candidate = ''.join(temp)
                    if candidate < result:
                        result = candidate
    return result if min_operations != sys.maxsize else ""
