def hasSpecialSubstring(s, k):
    n = len(s)
    for i in range(n - k + 1):
        current = s[i]
        valid = True
        for j in range(1, k):
            if s[i + j] != current:
                valid = False
                break
        if not valid:
            continue
        before_valid = True
        if i > 0:
            if s[i - 1] == current:
                before_valid = False
        if not before_valid:
            continue
        after_valid = True
        if i + k < n:
            if s[i + k] == current:
                after_valid = False
        if before_valid and after_valid and valid:
            return True
    return False
