import bisect

def separateSquares(squares):
    def compute_area(y_line):
        total = 0.0
        for x, y, l in squares:
            if y + l <= y_line:
                total += l * l
            elif y < y_line:
                total += (y_line - y) * l
        return total

    y_coords = []
    for x, y, l in squares:
        y_coords.append(y)
        y_coords.append(y + l)
    y_coords.sort()
    total_area = sum(l * l for x, y, l in squares)
    target = total_area / 2.0

    left = 0.0
    right = float(max(y_coords))
    for _ in range(100):
        mid = (left + right) / 2.0
        area = compute_area(mid)
        if area < target:
            left = mid
        else:
            right = mid
    return left
