import sys

def minZeroArray(nums, queries):
    n = len(nums)
    m = len(queries)
    prefix = [0] * (n + 1)
    for k in range(1, m + 1):
        l, r, val = queries[k-1]
        prefix[l] += val
        if r + 1 < n:
            prefix[r+1] -= val
        current = 0
        possible = True
        for i in range(n):
            current += prefix[i]
            if nums[i] < current:
                possible = False
                break
        if possible:
            current = 0
            temp = nums.copy()
            for i in range(n):
                current += prefix[i]
                temp[i] -= current
                if temp[i] < 0:
                    possible = False
                    break
            if possible and all(x == 0 for x in temp):
                return k
    return -1
