import bisect

def candidate_func(input_str):
    lines = input_str.split('\n')
    N, Q = map(int, lines[0].split())
    A = list(map(int, lines[1].split()))
    queries = []
    for i in range(Q):
        R, X = map(int, lines[2 + i].split())
        queries.append((R, X))

    results = []
    for R, X in queries:
        sub_A = A[:R]
        lis = []
        for num in sub_A:
            if num > X:
                continue
            idx = bisect.bisect_left(lis, num)
            if idx == len(lis):
                lis.append(num)
            else:
                lis[idx] = num
        results.append(str(len(lis)))
    return '\n'.join(results)
