import sys

def candidate_func(input_str):
    N = int(input_str.strip())
    grid = [['.' for _ in range(N)] for _ in range(N)]
    for i in range(1, N + 1):
        j = N + 1 - i
        if i > j:
            continue
        start = i - 1
        end = j - 1
        for x in range(start, end + 1):
            for y in range(start, end + 1):
                if i % 2 == 1:
                    grid[x][y] = '#'
                else:
                    grid[x][y] = '.'
    return '\n'.join(''.join(row) for row in grid)
