import sys
from collections import defaultdict

def candidate_func(input_str):
    lines = input_str.split('\n')
    N = int(lines[0])
    S = lines[1]
    T = lines[2]
    if S == T:
        return '0'
    graph = defaultdict(set)
    for s_char, t_char in zip(S, T):
        if s_char != t_char:
            graph[s_char].add(t_char)
    visited = set()
    operations = 0
    for char in graph:
        if char not in visited:
            current_visited = set()
            stack = [char]
            is_cycle = False
            while stack:
                node = stack.pop()
                if node in current_visited:
                    is_cycle = True
                    break
                if node in visited:
                    break
                current_visited.add(node)
                visited.add(node)
                for neighbor in graph[node]:
                    stack.append(neighbor)
            if is_cycle:
                return '-1'
            operations += len(current_visited) - 1
    return str(operations)
