import math

def candidate_func(input_str):
    N = int(input_str)
    seen = set()
    max_b = int(math.isqrt(N)) + 2
    for b in range(1, max_b):
        max_a = int(math.log2(N / (b * b))) + 2
        for a in range(1, max_a):
            x = (1 << a) * b * b
            if x <= N:
                seen.add(x)
    return str(len(seen))
