import sys

def candidate_func(input_str):
    lines = input_str.split('\n')
    N = int(lines[0])
    A = list(map(int, lines[1].split()))
    stack = []
    dp = [-float('inf')] * (N + 1)
    dp[0] = 0
    for i in range(1, N + 1):
        if i >= 1:
            dp[i] = dp[i - 1] + A[i - 1]
        if i >= 2:
            dp[i] = max(dp[i], dp[i - 2] + A[i - 1])
        if i >= 3:
            dp[i] = max(dp[i], dp[i - 3] + A[i - 1])
        if i >= 4:
            dp[i] = max(dp[i], dp[i - 4] + A[i - 1])
    max_sum = max(dp[N], dp[N - 1] if N >= 1 else -float('inf'), dp[N - 2] if N >= 2 else -float('inf'), dp[N - 3] if N >= 3 else -float('inf'))
    return str(max_sum)
