def candidate_func(input_str):
    import sys
    lines = input_str.strip().split('\n')
    N = int(lines[0])
    P = list(map(int, lines[1].split()))
    cost = 0
    for i in range(1, N+1):
        if P[i-1] == i:
            if i < N and P[i] == i+1:
                cost += i
                P[i-1], P[i] = P[i], P[i-1]
    for i in range(N-1, 0, -1):
        if P[i-1] == i+1 and P[i] == i:
            cost += i
            P[i-1], P[i] = P[i], P[i-1]
    for i in range(N):
        if P[i] != i+1:
            for j in range(i, N):
                if P[j] == i+1:
                    cost += sum(range(i+1, j+1))
                    P[i:j+1] = [P[j]] + P[i:j]
                    break
    return str(cost)
