import sys
from collections import deque

def candidate_func(input_str):
    lines = input_str.split('\n')
    N, X, Y = map(int, lines[0].split())
    S = lines[1].strip()
    T = lines[2].strip()

    if S == T:
        return "Yes"

    if X > Y:
        X, Y = Y, X

    diff_indices = [i for i in range(N) if S[i] != T[i]]
    if not diff_indices:
        return "Yes"

    first_diff = diff_indices[0]
    last_diff = diff_indices[-1]

    if last_diff - first_diff + 1 < X + Y:
        return "No"

    for i in range(first_diff, last_diff - (X + Y) + 2):
        if S[i:i+X] == '0' * X and S[i+X:i+X+Y] == '1' * Y:
            return "Yes"
        if S[i:i+Y] == '1' * Y and S[i+Y:i+Y+X] == '0' * X:
            return "Yes"

    return "No"
