def minCostGoodCaption(caption):
    n = len(caption)
    if n < 3:
        return ""

    result = list(caption)
    i = 0

    while i < n:
        j = i
        while j < n and caption[j] == caption[i]:
            j += 1

        length = j - i
        if length < 3:
            if i > 0 and j < n:
                if caption[i - 1] == caption[j]:
                    for k in range(i, j):
                        result[k] = caption[i - 1]
                else:
                    for k in range(i, j):
                        result[k] = min(caption[i - 1], caption[j])
            elif i > 0:
                for k in range(i, j):
                    result[k] = caption[i - 1]
            elif j < n:
                for k in range(i, j):
                    result[k] = caption[j]
            else:
                return ""

        i = j

    return "".join(result)
