import heapq

def maxSum(nums, k, m):
    n = len(nums)
    dp = [[-float('inf')] * (k + 1) for _ in range(n + 1)]
    dp[0][0] = 0
    for i in range(m, n + 1):
        current_sum = sum(nums[i - m:i])
        max_heap = [(-current_sum, i - m)]
        for j in range(i - m + 1, i):
            current_sum += nums[j]
            heapq.heappush(max_heap, (-current_sum, j))
        for j in range(1, k + 1):
            while max_heap and max_heap[0][1] < i - m:
                heapq.heappop(max_heap)
            if max_heap:
                dp[i][j] = max(dp[i][j], dp[i - m][j - 1] - max_heap[0][0])
    return max(dp[n])
