def longestPalindromicSubsequence(s, k):
    n = len(s)
    dp = [[0] * n for _ in range(n)]

    for i in range(n):
        dp[i][i] = 1

    for length in range(2, n + 1):
        for i in range(n - length + 1):
            j = i + length - 1
            if s[i] == s[j]:
                dp[i][j] = dp[i + 1][j - 1] + 2
            else:
                dp[i][j] = max(dp[i + 1][j], dp[i][j - 1])

    def canTransform(x, y):
        diff = abs(ord(x) - ord(y))
        return min(diff, 26 - diff)

    max_len = 0
    for i in range(n):
        for j in range(i, n):
            if dp[i][j] == j - i + 1:
                cost = 0
                for l in range((j - i + 1) // 2):
                    cost += canTransform(s[i + l], s[j - l])
                if cost <= k:
                    max_len = max(max_len, j - i + 1)

    return max_len
