def candidate_func(input_str):
    import sys
    from collections import defaultdict, deque

    input_data = input_str.strip().split('\n')
    N, M = map(int, input_data[0].split())

    edges = defaultdict(list)
    for i in range(1, M + 1):
        x, y, z = map(int, input_data[i].split())
        edges[x - 1].append((y - 1, z))
        edges[y - 1].append((x - 1, z))

    A = [-1] * N
    for start in range(N):
        if A[start] == -1:
            A[start] = 0
            queue = deque([start])
            while queue:
                node = queue.popleft()
                for neighbor, z in edges[node]:
                    if A[neighbor] == -1:
                        A[neighbor] = A[node] ^ z
                        queue.append(neighbor)
                    elif A[neighbor] != A[node] ^ z:
                        return "-1"

    return ' '.join(map(str, A))
