def candidate_func(input_str):
    import sys
    input = sys.stdin.read
    data = input_str.split()

    index = 0
    T = int(data[index])
    index += 1
    results = []

    for _ in range(T):
        N = int(data[index])
        index += 1
        A = list(map(int, data[index:index + 2 * N]))
        index += 2 * N

        positions = {}
        for i in range(2 * N):
            if A[i] not in positions:
                positions[A[i]] = []
            positions[A[i]].append(i)

        count = 0
        for a in range(1, N + 1):
            for b in range(a + 1, N + 1):
                a1, a2 = positions[a]
                b1, b2 = positions[b]

                if abs(a1 - a2) > 1 and abs(b1 - b2) > 1:
                    if (a1 < b1 < a2 < b2) or (b1 < a1 < b2 < a2):
                        count += 1

        results.append(str(count))

    return "\n".join(results)
