import sys
import math

def candidate_func(input_str):
    input_data = input_str.strip().split()
    Q = int(input_data[0])
    queries = list(map(int, input_data[1:]))

    def is_400_number(n):
        prime_factors = []
        for i in range(2, int(math.sqrt(n)) + 1):
            if n % i == 0:
                count = 0
                while n % i == 0:
                    n //= i
                    count += 1
                if count % 2 != 0:
                    return False
                prime_factors.append(i)
        if n > 1:
            prime_factors.append(n)
        return len(prime_factors) == 2

    def largest_400_number(a):
        for num in range(a, 35, -1):
            if is_400_number(num):
                return num
        return 36

    results = [str(largest_400_number(a)) for a in queries]
    return "\n".join(results)
