import sys
from collections import defaultdict, deque

def candidate_func(input_str):
    data = input_str.split()
    index = 0
    N = int(data[index])
    index += 1
    weights = list(map(int, data[index:index + N]))
    index += N
    intervals = []
    for _ in range(N):
        L = int(data[index])
        R = int(data[index + 1])
        intervals.append((L, R))
        index += 2
    Q = int(data[index])
    index += 1
    queries = []
    for _ in range(Q):
        s = int(data[index]) - 1
        t = int(data[index + 1]) - 1
        queries.append((s, t))
        index += 2

    def intersect(l1, r1, l2, r2):
        return not (r1 < l2 or r2 < l1)

    graph = defaultdict(list)
    for i in range(N):
        for j in range(i + 1, N):
            if not intersect(intervals[i][0], intervals[i][1], intervals[j][0], intervals[j][1]):
                graph[i].append(j)
                graph[j].append(i)

    def bfs_min_weight_path(start, end):
        if start == end:
            return weights[start]
        visited = [False] * N
        queue = deque([(start, weights[start])])
        visited[start] = True
        while queue:
            node, current_weight = queue.popleft()
            for neighbor in graph[node]:
                if not visited[neighbor]:
                    if neighbor == end:
                        return current_weight + weights[neighbor]
                    visited[neighbor] = True
                    queue.append((neighbor, current_weight + weights[neighbor]))
        return -1

    results = []
    for s, t in queries:
        result = bfs_min_weight_path(s, t)
        results.append(str(result))

    return "\n".join(results)
