def candidate_func(input_str):
    import sys
    input = sys.stdin.read
    data = input_str.strip().split()

    N = int(data[0])
    A = list(map(int, data[1:N+1]))
    B = list(map(int, data[N+1:2*N+1]))

    total_sum = 0
    known_pairs = 0

    for a, b in zip(A, B):
        if a != -1 and b != -1:
            total_sum += a + b
            known_pairs += 1

    if known_pairs == 0:
        return "Yes"

    target_sum = total_sum // known_pairs

    if total_sum % known_pairs != 0:
        return "No"

    for a, b in zip(A, B):
        if a != -1 and b != -1:
            if a + b != target_sum:
                return "No"

    return "Yes"
