def maxFreeTime(eventTime, k, startTime, endTime):
    meetings = sorted(zip(startTime, endTime))
    free_time = [0] * (len(meetings) + 1)
    for i in range(len(meetings) - 1):
        free_time[i + 1] = meetings[i + 1][0] - meetings[i][1]
    free_time[0] = meetings[0][0]
    free_time[-1] = eventTime - meetings[-1][1]
    free_time.sort(reverse=True)
    return sum(free_time[:k + 1])
