import sys

def minimumCost(nums, cost, k):
    n = len(nums)
    prefix_sum = [0] * (n + 1)
    for i in range(n):
        prefix_sum[i + 1] = prefix_sum[i] + nums[i]
    prefix_cost = [0] * (n + 1)
    for i in range(n):
        prefix_cost[i + 1] = prefix_cost[i] + cost[i]
    memo = {}

    def dp(i, j):
        if (i, j) in memo:
            return memo[(i, j)]
        if i == j:
            return (prefix_sum[j] - prefix_sum[i] + k * (j - i + 1)) * (prefix_cost[j] - prefix_cost[i])
        res = sys.maxsize
        for x in range(i, j):
            res = min(res, dp(i, x) + dp(x + 1, j))
        memo[(i, j)] = res
        return res

    return dp(0, n - 1)
