def numOfUnplacedFruits(fruits, baskets):
    baskets = sorted(enumerate(baskets), key=lambda x: x[1])
    unplaced = 0
    for fruit in fruits:
        placed = False
        for i, basket in baskets:
            if basket >= fruit:
                baskets[i] = (i, 0)
                placed = True
                break
        if not placed:
            unplaced += 1
    return unplaced
