def minTime(skill, mana):
    n, m = len(skill), len(mana)
    dp = [[0] * m for _ in range(n)]
    for j in range(m):
        dp[0][j] = skill[0] * mana[j]
    for i in range(1, n):
        for j in range(m):
            dp[i][j] = max(dp[i-1][j], (dp[i][j-1] if j > 0 else 0)) + skill[i] * mana[j]
    return dp[-1][-1]
