import sys

def candidate_func(input_str):
    lines = input_str.split()
    N = int(lines[0])
    A = list(map(int, lines[1:]))
    j = N - 1
    i = N - 2
    ans = 0
    while i >= 0 and j >= 0:
        if A[i] * 2 <= A[j]:
            ans += 1
            i -= 1
            j -= 1
        else:
            i -= 1
    return str(ans)
