import sys

def candidate_func(input_str):
    lines = input_str.split('\n')
    N = int(lines[0])
    P = list(map(int, lines[1].split()))
    Q = list(map(int, lines[2].split()))
    Q_index = [0] * (N + 1)
    for i in range(N):
        Q_index[Q[i]] = i + 1
    result = []
    for i in range(1, N + 1):
        result.append(str(Q[P[Q_index[i] - 1] - 1]))
    return ' '.join(result)
