from collections import defaultdict

def candidate_func(input_str):
    lines = input_str.splitlines()
    n = int(lines[0])
    edges = []
    for line in lines[1:]:
        u, v = map(int, line.split())
        edges.append((u, v))
    graph = defaultdict(list)
    for u, v in edges:
        graph[u].append(v)
        graph[v].append(u)
    degrees = {u: 0 for u in range(1, n + 1)}
    for u in graph:
        degrees[u] = len(graph[u])
    max_vertices = -1
    for u in graph:
        if degrees[u] == 4:
            visited = set()
            stack = [(u, 0)]
            while stack:
                node, depth = stack.pop()
                if node not in visited:
                    visited.add(node)
                    for neighbor in graph[node]:
                        if degrees[neighbor] in [1, 4] and neighbor not in visited:
                            stack.append((neighbor, depth + 1))
            max_vertices = max(max_vertices, len(visited))
    return str(max_vertices)
