from sys import stdin

def candidate_func(input_str):
    lines = input_str.splitlines()
    N = int(lines[0])
    A = list(map(int, lines[1].split()))
    min_length = float('inf')
    for i in range(N):
        for j in range(i + 1, N + 1):
            subarray = A[i:j]
            if len(subarray) == len(set(subarray)):
                continue
            for k in range(len(subarray)):
                for last in range(k + 1, len(subarray) + 1):
                    sub = subarray[k:last]
                    if len(sub) == len(set(sub)):
                        continue
                    if len(sub) < min_length:
                        min_length = len(sub)
    if min_length == float('inf'):
        return '-1'
    return str(min_length)

input_str = stdin.read()
print(candidate_func(input_str))
