from sys import stdin
from collections import defaultdict
from itertools import permutations

def candidate_func(input_str):
    lines = input_str.splitlines()
    N, M = map(int, lines[0].split())
    edges = []
    for line in lines[1:]:
        u, v, w = map(int, line.split())
        edges.append((u, v, w))
    graph = defaultdict(list)
    for u, v, w in edges:
        graph[u].append((v, w))
        graph[v].append((u, w))
    min_xor = float('inf')
    for path in permutations(range(2, N+1)):
        path = (1,) + path + (N,)
        xor = 0
        valid = True
        for i in range(len(path) - 1):
            found = False
            for v, w in graph[path[i]]:
                if v == path[i+1]:
                    xor ^= w
                    found = True
                    break
            if not found:
                valid = False
                break
        if valid:
            min_xor = min(min_xor, xor)
    return str(min_xor)

print(candidate_func(stdin.read()))
