import math

def candidate_func(s):
    lines = s.splitlines()
    Q = int(lines[0])
    queries = [int(line) for line in lines[1:]]
    def is_prime(n):
        if n < 2:
            return False
        for i in range(2, int(math.sqrt(n)) + 1):
            if n % i == 0:
                return False
        return True
    def prime_factors(n):
        factors = {}
        i = 2
        while i * i <= n:
            if n % i:
                i += 1
            else:
                n //= i
                factors[i] = factors.get(i, 0) + 1
        if n > 1:
            factors[n] = factors.get(n, 0) + 1
        return factors
    def is_400_number(n):
        factors = prime_factors(n)
        return len(factors) == 2 and all(count % 2 == 0 for count in factors.values())
    def largest_400_number_not_exceeding(n):
        for i in range(n, 0, -1):
            if is_400_number(i):
                return i
    return '\n'.join(str(largest_400_number_not_exceeding(query)) for query in queries)
