import sys

def candidate_func(input_str):
    lines = input_str.splitlines()
    n = int(lines[0])
    a = list(map(int, lines[1].split()))
    b = list(map(int, lines[2].split()))
    sums = []
    for i in range(n):
        if a[i] == -1 and b[i] == -1:
            sums.append(0)
        elif a[i] == -1:
            sums.append(b[i])
        elif b[i] == -1:
            sums.append(a[i])
        else:
            sums.append(a[i] + b[i])
    s = set(sums)
    if len(s) > 2:
        return "No"
    elif len(s) == 2:
        if -1 in a and -1 in b:
            return "Yes"
        else:
            return "No"
    else:
        return "Yes"

input_str = sys.stdin.read()
print(candidate_func(input_str))
