from collections import defaultdict

def solveQueries(nums, queries):
    index_map = defaultdict(list)
    for idx, num in enumerate(nums):
        index_map[num].append(idx)

    answer = []
    for query in queries:
        num = nums[query]
        if num in index_map and len(index_map[num]) > 1:
            indices = index_map[num]
            min_dist = float('inf')
            for idx in indices:
                if idx == query:
                    continue
                dist = min(abs(query - idx), len(nums) - abs(query - idx))
                if dist < min_dist:
                    min_dist = dist
            answer.append(min_dist)
        else:
            answer.append(-1)
    return answer
