def maxSubstringLength(s: str, k: int) -> bool:
    from collections import defaultdict

    n = len(s)
    if k == 0:
        return True
    if k > 26:
        return False

    freq = defaultdict(int)
    for c in s:
        freq[c] += 1

    left = 0
    unique_chars = 0
    res = 0
    for right in range(n):
        if freq[s[right]] == 1:
            unique_chars += 1
        freq[s[right]] -= 1
        while freq[s[right]] == 0:
            unique_chars -= 1
            freq[s[left]] += 1
            left += 1
        res = max(res, right - left + 1)

    return res >= k
