import math

def maxDistance(side, points, k):
    points.sort()
    n = len(points)
    res = 0
    for i in range(n - k + 1):
        left = i
        right = i + k - 1
        min_dist = float('inf')
        for j in range(left, right):
            dist = abs(points[j][0] - points[j + 1][0]) + abs(points[j][1] - points[j + 1][1])
            if dist < min_dist:
                min_dist = dist
        if min_dist > res:
            res = min_dist
    return res
