def longestPalindromicSubsequence(s: str, k: int) -> int:
    n = len(s)
    dp = [[0] * n for _ in range(n)]

    for i in range(n-1, -1, -1):
        dp[i][i] = 1
        for j in range(i+1, n):
            if s[i] == s[j]:
                dp[i][j] = 2 + dp[i+1][j-1] if i+1 <= j-1 else 2
            else:
                dp[i][j] = max(dp[i+1][j], dp[i][j-1])

    def get_max_palindrome(s, k):
        if k == 0:
            return dp[0][n-1]
        res = 0
        for i in range(n):
            for c in [chr((ord(s[i]) - 1 - ord('a')) % 26 + ord('a')), chr((ord(s[i]) + 1 - ord('a')) % 26 + ord('a'))]:
                if c != s[i]:
                    temp = s[i]
                    s = s[:i] + c + s[i+1:]
                    res = max(res, get_max_palindrome(s, k-1))
                    s = s[:i] + temp + s[i+1:]
        return max(res, dp[0][n-1])

    return get_max_palindrome(s, k)
