def maxActiveSectionsAfterTrade(s: str) -> int:
    t = '1' + s + '1'
    n = len(t)
    res = 0
    for i in range(1, n - 1):
        if t[i] == '1':
            left = i - 1
            while left >= 0 and t[left] == '1':
                left -= 1
            right = i + 1
            while right < n and t[right] == '1':
                right += 1
            if left >= 0 and t[left] == '0' and right < n and t[right] == '0':
                res = max(res, sum(c == '1' for c in t[left + 1:right]))
    return res
