import math

def candidate_func(s):
    X = int(s)
    low = 2
    high = 20
    while low <= high:
        mid = (low + high) // 2
        fact = 1
        for i in range(1, mid + 1):
            fact *= i
        if fact == X:
            return str(mid)
        elif fact < X:
            low = mid + 1
        else:
            high = mid - 1
    return str(low - 1)
