import sys
from math import comb
from collections import deque

def candidate_func(s):
    N, P = map(int, s.split())
    max_edges = N * (N - 1) // 2
    min_edges = N - 1
    result = []
    for M in range(min_edges, max_edges + 1):
        total = 0
        for mask in range(1 << N):
            if bin(mask).count('1') != N:
                continue
            adj = [[] for _ in range(N)]
            edges = 0
            for i in range(N):
                for j in range(i + 1, N):
                    if (mask >> j) & 1 and (mask >> i) & 1:
                        adj[i].append(j)
                        adj[j].append(i)
                        edges += 1
                        if edges > M:
                            break
                if edges > M:
                    break
            if edges != M:
                continue
            if not is_connected(adj):
                continue
            even = 0
            odd = 0
            dist = bfs(adj, 0)
            for d in dist:
                if d % 2 == 0:
                    even += 1
                else:
                    odd += 1
            if even == odd:
                total += 1
        result.append(str(total % P))
    return ' '.join(result)

def is_connected(adj):
    visited = [False] * len(adj)
    q = deque([0])
    visited[0] = True
    while q:
        u = q.popleft()
        for v in adj[u]:
            if not visited[v]:
                visited[v] = True
                q.append(v)
    return all(visited)

def bfs(adj, start):
    dist = [-1] * len(adj)
    q = deque([(start, 0)])
    dist[start] = 0
    while q:
        u, d = q.popleft()
        for v in adj[u]:
            if dist[v] == -1:
                dist[v] = d + 1
                q.append((v, d + 1))
    return dist
