from collections import defaultdict

def candidate_func(s):
    lines = s.strip().split('\n')
    N = int(lines[0])
    dice = []
    for line in lines[1:]:
        parts = line.split()
        K = int(parts[0])
        faces = list(map(int, parts[1:K+1]))
        dice.append((K, faces))

    max_prob = 0.0
    for i in range(N):
        K1, faces1 = dice[i]
        freq1 = defaultdict(int)
        for num in faces1:
            freq1[num] += 1
        for j in range(i+1, N):
            K2, faces2 = dice[j]
            freq2 = defaultdict(int)
            for num in faces2:
                freq2[num] += 1
            common = 0
            for num in freq1:
                if num in freq2:
                    common += min(freq1[num], freq2[num])
            prob = common / (K1 * K2)
            if prob > max_prob:
                max_prob = prob
    return str(max_prob)
