def candidate_func(s):
    lines = s.split('\n')
    N = int(lines[0])
    A = list(map(int, lines[1].split()))
    prefix = [0] * (N + 1)
    for i in range(1, N + 1):
        prefix[i] = prefix[i - 1]
        if i > 0 and A[i - 1] not in prefix[:i]:
            prefix[i] += 1
    max_sum = 0
    for i in range(1, N - 1):
        distinct1 = prefix[i]
        for j in range(i + 1, N):
            distinct2 = prefix[j] - prefix[i]
            distinct3 = prefix[N] - prefix[j]
            current_sum = distinct1 + distinct2 + distinct3
            if current_sum > max_sum:
                max_sum = current_sum
    return str(max_sum)
